schema:
{
 "description": "EKSAnywhereSubscription is the Schema for the EKSAnywhereSubscriptions API",
 "properties": {
  "spec": {
   "description": "EksAnywhereSubscriptionSpec defines the desired state of EksAnywhereSubscription. \n An EKS Anywhere subscription authorizing the customer to support for licensed clusters and access to EKS Anywhere Curated Packages.",
   "properties": {
    "autoRenew": {
     "description": "A boolean indicating whether the subscription auto renews at the end of the term.",
     "type": "boolean"
    },
    "clientRequestToken": {
     "description": "Unique, case-sensitive identifier that you provide to ensure the idempotency of the request.",
     "type": "string"
    },
    "licenseQuantity": {
     "description": "The number of licenses to purchase with the subscription. Valid values are between 1 and 100. This value can't be changed after creating the subscription.",
     "format": "int64",
     "type": "integer"
    },
    "licenseType": {
     "description": "The license type for all licenses in the subscription. Valid value is CLUSTER. With the CLUSTER license type, each license covers support for a single EKS Anywhere cluster.",
     "type": "string"
    },
    "name": {
     "description": "The unique name for your subscription. It must be unique in your Amazon Web Services account in the Amazon Web Services Region you're creating the subscription in. The name can contain only alphanumeric characters (case-sensitive), hyphens, and underscores. It must start with an alphabetic character and can't be longer than 100 characters.",
     "type": "string"
    },
    "tags": {
     "additionalProperties": {
      "type": "string"
     },
     "description": "The metadata for a subscription to assist with categorization and organization. Each tag consists of a key and an optional value. Subscription tags don't propagate to any other resources associated with the subscription.",
     "type": "object"
    },
    "term": {
     "description": "An object representing the term duration and term unit type of your subscription. This determines the term length of your subscription. Valid values are MONTHS for term unit and 12 or 36 for term duration, indicating a 12 month or 36 month subscription. This value cannot be changed after creating the subscription.",
     "properties": {
      "duration": {
       "format": "int64",
       "type": "integer"
      },
      "unit": {
       "type": "string"
      }
     },
     "type": "object"
    }
   },
   "required": [
    "name",
    "term"
   ],
   "type": "object"
  }
 },
 "title": "EKS Anywhere Subscription",
 "type": "object"
}
