schema:
{
 "description": "EventBus is the Schema for the EventBuses API",
 "properties": {
  "spec": {
   "description": "EventBusSpec defines the desired state of EventBus. \n An event bus receives events from a source, uses rules to evaluate them, applies any configured input transformation, and routes them to the appropriate target(s). Your account's default event bus receives events from Amazon Web Services services. A custom event bus can receive events from your custom applications and services. A partner event bus receives events from an event source created by an SaaS partner. These events come from the partners services or applications.",
   "properties": {
    "eventSourceName": {
     "description": "If you are creating a partner event bus, this specifies the partner event source that the new event bus will be matched with.",
     "type": "string"
    },
    "name": {
     "description": "The name of the new event bus. \n Custom event bus names can't contain the / character, but you can use the / character in partner event bus names. In addition, for partner event buses, the name must exactly match the name of the partner event source that this event bus is matched to. \n You can't use the name default for a custom event bus, as this name is already used for your account's default event bus.",
     "type": "string"
    },
    "tags": {
     "description": "Tags to associate with the event bus.",
     "items": {
      "description": "A key-value pair associated with an Amazon Web Services resource. In EventBridge, rules and event buses support tagging.",
      "properties": {
       "key": {
        "type": "string"
       },
       "value": {
        "type": "string"
       }
      },
      "type": "object"
     },
     "type": "array"
    }
   },
   "required": [
    "name"
   ],
   "type": "object"
  }
 },
 "title": "Event Bus",
 "type": "object"
}
