{
 "description": "ResolverEndpoint is the Schema for the ResolverEndpoints API",
 "properties": {
  "spec": {
   "description": "ResolverEndpointSpec defines the desired state of ResolverEndpoint. \n In the response to a CreateResolverEndpoint (https://docs.aws.amazon.com/Route53/latest/APIReference/API_route53resolver_CreateResolverEndpoint.html), DeleteResolverEndpoint (https://docs.aws.amazon.com/Route53/latest/APIReference/API_route53resolver_DeleteResolverEndpoint.html), GetResolverEndpoint (https://docs.aws.amazon.com/Route53/latest/APIReference/API_route53resolver_GetResolverEndpoint.html), Updates the name, or ResolverEndpointType for an endpoint, or UpdateResolverEndpoint (https://docs.aws.amazon.com/Route53/latest/APIReference/API_route53resolver_UpdateResolverEndpoint.html) request, a complex type that contains settings for an existing inbound or outbound Resolver endpoint.",
   "properties": {
    "direction": {
     "description": "Specify the applicable value: \n - INBOUND: Resolver forwards DNS queries to the DNS service for a VPC from your network \n - OUTBOUND: Resolver forwards DNS queries from the DNS service for a VPC to your network",
     "type": "string"
    },
    "ipAddresses": {
     "description": "The subnets and IP addresses in your VPC that DNS queries originate from (for outbound endpoints) or that you forward DNS queries to (for inbound endpoints). The subnet ID uniquely identifies a VPC.",
     "items": {
      "description": "In a CreateResolverEndpoint (https://docs.aws.amazon.com/Route53/latest/APIReference/API_route53resolver_CreateResolverEndpoint.html) request, the IP address that DNS queries originate from (for outbound endpoints) or that you forward DNS queries to (for inbound endpoints). IpAddressRequest also includes the ID of the subnet that contains the IP address.",
      "properties": {
       "ip": {
        "type": "string"
       },
       "ipv6": {
        "type": "string"
       },
       "subnetID": {
        "type": "string"
       },
       "subnetRef": {
        "description": "Reference field for SubnetID",
        "properties": {
         "from": {
          "description": "AWSResourceReference provides all the values necessary to reference another k8s resource for finding the identifier(Id/ARN/Name)",
          "properties": {
           "name": {
            "type": "string"
           }
          },
          "type": "object"
         }
        },
        "type": "object"
       }
      },
      "type": "object"
     },
     "type": "array"
    },
    "name": {
     "description": "A friendly name that lets you easily find a configuration in the Resolver dashboard in the Route 53 console.",
     "type": "string"
    },
    "resolverEndpointType": {
     "description": "For the endpoint type you can choose either IPv4, IPv6. or dual-stack. A dual-stack endpoint means that it will resolve via both IPv4 and IPv6. This endpoint type is applied to all IP addresses.",
     "type": "string"
    },
    "securityGroupIDs": {
     "description": "The ID of one or more security groups that you want to use to control access to this VPC. The security group that you specify must include one or more inbound rules (for inbound Resolver endpoints) or outbound rules (for outbound Resolver endpoints). Inbound and outbound rules must allow TCP and UDP access. For inbound access, open port 53. For outbound access, open the port that you're using for DNS queries on your network.",
     "items": {
      "type": "string"
     },
     "type": "array"
    },
    "securityGroupRefs": {
     "items": {
      "description": "AWSResourceReferenceWrapper provides a wrapper around *AWSResourceReference type to provide more user friendly syntax for references using 'from' field Ex: APIIDRef: \n from: name: my-api",
      "properties": {
       "from": {
        "description": "AWSResourceReference provides all the values necessary to reference another k8s resource for finding the identifier(Id/ARN/Name)",
        "properties": {
         "name": {
          "type": "string"
         }
        },
        "type": "object"
       }
      },
      "type": "object"
     },
     "type": "array"
    },
    "tags": {
     "description": "A list of the tag keys and values that you want to associate with the endpoint.",
     "items": {
      "description": "One tag that you want to add to the specified resource. A tag consists of a Key (a name for the tag) and a Value.",
      "properties": {
       "key": {
        "type": "string"
       },
       "value": {
        "type": "string"
       }
      },
      "type": "object"
     },
     "type": "array"
    }
   },
   "required": [
    "direction",
    "ipAddresses"
   ],
   "type": "object"
  }
 },
