schema:
{
 "description": "ResolverRule is the Schema for the ResolverRules API",
 "properties": {
  "spec": {
   "description": "ResolverRuleSpec defines the desired state of ResolverRule. \n For queries that originate in your VPC, detailed information about a Resolver rule, which specifies how to route DNS queries out of the VPC. The ResolverRule parameter appears in the response to a CreateResolverRule (https://docs.aws.amazon.com/Route53/latest/APIReference/API_route53resolver_CreateResolverRule.html), DeleteResolverRule (https://docs.aws.amazon.com/Route53/latest/APIReference/API_route53resolver_DeleteResolverRule.html), GetResolverRule (https://docs.aws.amazon.com/Route53/latest/APIReference/API_route53resolver_GetResolverRule.html), ListResolverRules (https://docs.aws.amazon.com/Route53/latest/APIReference/API_route53resolver_ListResolverRules.html), or UpdateResolverRule (https://docs.aws.amazon.com/Route53/latest/APIReference/API_route53resolver_UpdateResolverRule.html) request.",
   "properties": {
    "associations": {
     "items": {
      "description": "In the response to an AssociateResolverRule (https://docs.aws.amazon.com/Route53/latest/APIReference/API_route53resolver_AssociateResolverRule.html), DisassociateResolverRule (https://docs.aws.amazon.com/Route53/latest/APIReference/API_route53resolver_DisassociateResolverRule.html), or ListResolverRuleAssociations (https://docs.aws.amazon.com/Route53/latest/APIReference/API_route53resolver_ListResolverRuleAssociations.html) request, provides information about an association between a Resolver rule and a VPC. The association determines which DNS queries that originate in the VPC are forwarded to your network.",
      "properties": {
       "id": {
        "type": "string"
       },
       "name": {
        "type": "string"
       },
       "resolverRuleID": {
        "type": "string"
       },
       "status": {
        "type": "string"
       },
       "statusMessage": {
        "type": "string"
       },
       "vpcID": {
        "type": "string"
       }
      },
      "type": "object"
     },
     "type": "array"
    },
    "domainName": {
     "description": "DNS queries for this domain name are forwarded to the IP addresses that you specify in TargetIps. If a query matches multiple Resolver rules (example.com and www.example.com), outbound DNS queries are routed using the Resolver rule that contains the most specific domain name (www.example.com).",
     "type": "string"
    },
    "name": {
     "description": "A friendly name that lets you easily find a rule in the Resolver dashboard in the Route 53 console.",
     "type": "string"
    },
    "resolverEndpointID": {
     "description": "The ID of the outbound Resolver endpoint that you want to use to route DNS queries to the IP addresses that you specify in TargetIps.",
     "type": "string"
    },
    "ruleType": {
     "description": "When you want to forward DNS queries for specified domain name to resolvers on your network, specify FORWARD. \n When you have a forwarding rule to forward DNS queries for a domain to your network and you want Resolver to process queries for a subdomain of that domain, specify SYSTEM. \n For example, to forward DNS queries for example.com to resolvers on your network, you create a rule and specify FORWARD for RuleType. To then have Resolver process queries for apex.example.com, you create a rule and specify SYSTEM for RuleType. \n Currently, only Resolver can create rules that have a value of RECURSIVE for RuleType.",
     "type": "string"
    },
    "tags": {
     "description": "A list of the tag keys and values that you want to associate with the endpoint.",
     "items": {
      "description": "One tag that you want to add to the specified resource. A tag consists of a Key (a name for the tag) and a Value.",
      "properties": {
       "key": {
        "type": "string"
       },
       "value": {
        "type": "string"
       }
      },
      "type": "object"
     },
     "type": "array"
    },
    "targetIPs": {
     "description": "The IPs that you want Resolver to forward DNS queries to. You can specify only IPv4 addresses. Separate IP addresses with a space. \n TargetIps is available only when the value of Rule type is FORWARD.",
     "items": {
      "description": "In a CreateResolverRule (https://docs.aws.amazon.com/Route53/latest/APIReference/API_route53resolver_CreateResolverRule.html) request, an array of the IPs that you want to forward DNS queries to.",
      "properties": {
       "ip": {
        "type": "string"
       },
       "ipv6": {
        "type": "string"
       },
       "port": {
        "format": "int64",
        "type": "integer"
       }
      },
      "type": "object"
     },
     "type": "array"
    }
   },
   "required": [
    "domainName",
    "ruleType"
   ],
   "type": "object"
  }
 },
 "title": "Resolver Rule",
 "type": "object"
}
