schema:
{
 "properties": {
  "addTimestampToBackupDirectory": {
   "default": true,
   "type": "boolean"
  },
  "backupProfile": {
   "description": "backupProfile specification if backupProfileName is not specified",
   "properties": {
    "dumpInstance": {
     "properties": {
      "dumpOptions": {
       "description": "A dictionary of key-value pairs passed directly to MySQL Shell's DumpInstance()",
       "type": "object",
       "x-kubernetes-preserve-unknown-fields": true
      },
      "storage": {
       "properties": {
        "ociObjectStorage": {
         "properties": {
          "bucketName": {
           "description": "Bucket name where backup is stored",
           "type": "string"
          },
          "credentials": {
           "description": "Secret name with data for accessing the bucket",
           "type": "string"
          },
          "prefix": {
           "description": "Path in bucket where backup is stored",
           "type": "string"
          }
         },
         "required": [
          "bucketName",
          "credentials"
         ],
         "type": "object"
        },
        "persistentVolumeClaim": {
         "description": "Specification of the PVC to be used. Used 'as is' in pod executing the backup.",
         "type": "object",
         "x-kubernetes-preserve-unknown-fields": true
        },
        "s3": {
         "properties": {
          "bucketName": {
           "description": "Name of the bucket where the dump is stored",
           "type": "string"
          },
          "config": {
           "description": "Secret name with S3 configuration and credentials",
           "type": "string"
          },
          "endpoint": {
           "description": "Override endpoint URL",
           "type": "string"
          },
          "prefix": {
           "description": "Path in the bucket where the dump files are stored",
           "type": "string"
          },
          "profile": {
           "default": "",
           "description": "Profile being used in configuration files",
           "type": "string"
          }
         },
         "required": [
          "bucketName",
          "config"
         ],
         "type": "object"
        }
       },
       "type": "object",
       "x-kubernetes-preserve-unknown-fields": true
      }
     },
     "type": "object"
    },
    "podAnnotations": {
     "type": "object",
     "x-kubernetes-preserve-unknown-fields": true
    },
    "podLabels": {
     "type": "object",
     "x-kubernetes-preserve-unknown-fields": true
    }
   },
   "type": "object",
   "x-kubernetes-preserve-unknown-fields": true
  },
  "backupProfileName": {
   "type": "string"
  },
  "clusterName": {
   "type": "string"
  },
  "deleteBackupData": {
   "default": false,
   "type": "boolean"
  }
 },
 "required": [
  "clusterName"
 ],
 "title": "My SQL Backup",
 "type": "object"
}
