schema:
{
 "description": "ComponentSpec is the spec for a component",
 "properties": {
  "ignoreErrors": {
   "type": "boolean"
  },
  "initTimeout": {
   "type": "string"
  },
  "metadata": {
   "items": {
    "description": "MetadataItem is a name/value pair for a metadata",
    "properties": {
     "name": {
      "type": "string"
     },
     "secretKeyRef": {
      "description": "SecretKeyRef is a reference to a secret holding the value for the metadata item. Name is the secret name, and key is the field in the secret.",
      "properties": {
       "key": {
        "type": "string"
       },
       "name": {
        "type": "string"
       }
      },
      "required": [
       "key",
       "name"
      ],
      "type": "object"
     },
     "value": {
      "x-kubernetes-preserve-unknown-fields": true
     }
    },
    "required": [
     "name"
    ],
    "type": "object"
   },
   "type": "array"
  },
  "type": {
   "type": "string"
  },
  "version": {
   "type": "string"
  }
 },
 "required": [
  "metadata",
  "type",
  "version"
 ],
 "title": "Component",
 "type": "object"
}
