schema:
{
 "description": "ApiDefinition is the Schema for the apidefinitions API. The corresponding API definition version is 2.0.0. This version is still compatible with APIM 4.x but does not support v4 API features.",
 "properties": {
  "spec": {
   "description": "The API definition is the main resource handled by the Kubernetes Operator Most of the configuration properties defined here are already documented in the APIM Console API Reference. See https://docs.gravitee.io/apim/3.x/apim_installguide_rest_apis_documentation.html",
   "properties": {
    "contextRef": {
     "description": "The contextRef field allows to reference a previously created ManagementContext. This allows to sync API definitions with an APIM environment. See \u003ca href=\"#managementcontext\"\u003eManagementContext\u003c/a\u003e for more information. Omitting the namespace field will default to the namespace of the resource.",
     "properties": {
      "name": {
       "type": "string"
      },
      "namespace": {
       "type": "string"
      }
     },
     "required": [
      "name"
     ],
     "type": "object"
    },
    "crossId": {
     "description": "When promoting an API from one environment to the other, this ID identifies the API across those different environments. Setting this ID also allows to take control over an existing API on an APIM instance (by setting the same value as defined in APIM). If empty, a UUID will be generated based on the namespace and name of the resource.",
     "type": "string"
    },
    "definition_context": {
     "description": "The definition context is used to inform a management API instance that this API definition is managed using a kubernetes operator",
     "properties": {
      "mode": {
       "default": "fully_managed",
       "type": "string"
      },
      "origin": {
       "default": "kubernetes",
       "type": "string"
      }
     },
     "type": "object"
    },
    "deployedAt": {
     "description": "Shows the time that the API is deployed",
     "format": "int64",
     "type": "integer"
    },
    "description": {
     "description": "API description",
     "type": "string"
    },
    "flow_mode": {
     "default": "DEFAULT",
     "description": "The flow mode of the API. The value is either `DEFAULT` or `BEST_MATCH`.",
     "enum": [
      "DEFAULT",
      "BEST_MATCH"
     ],
     "type": "string"
    },
    "flows": {
     "description": "The flow of the API",
     "items": {
      "properties": {
       "condition": {
        "description": "Flow condition",
        "type": "string"
       },
       "consumers": {
        "description": "List of the consumers of this Flow",
        "items": {
         "properties": {
          "consumerId": {
           "description": "Consumer ID",
           "type": "string"
          },
          "consumerType": {
           "description": "Consumer type (possible values TAG)",
           "type": "integer"
          }
         },
         "type": "object"
        },
        "type": "array"
       },
       "enabled": {
        "default": true,
        "description": "Indicate if this flow is enabled or disabled",
        "type": "boolean"
       },
       "id": {
        "description": "Flow ID",
        "type": "string"
       },
       "methods": {
        "description": "A list of methods  for this flow (GET;POST;PUT;PATCH;DELETE;OPTIONS;HEAD;CONNECT;TRACE;OTHER)",
        "items": {
         "enum": [
          "GET",
          "POST",
          "PUT",
          "PATCH",
          "DELETE",
          "OPTIONS",
          "HEAD",
          "CONNECT",
          "TRACE",
          "OTHER"
         ],
         "type": "string"
        },
        "type": "array"
       },
       "name": {
        "description": "Flow name",
        "type": "string"
       },
       "path-operator": {
        "description": "List of path operators",
        "properties": {
         "operator": {
          "default": "STARTS_WITH",
          "description": "Operator (possible values STARTS_WITH or EQUALS)",
          "type": "string"
         },
         "path": {
          "description": "Operator path",
          "type": "string"
         }
        },
        "type": "object"
       },
       "post": {
        "description": "Flow post step",
        "items": {
         "properties": {
          "condition": {
           "description": "FlowStep condition",
           "type": "string"
          },
          "configuration": {
           "description": "FlowStep configuration is a map of arbitrary key-values",
           "format": "textarea",
           "type": "string"
          },
          "description": {
           "description": "FlowStep description",
           "type": "string"
          },
          "enabled": {
           "default": true,
           "description": "Indicate if this FlowStep is enabled or not",
           "type": "boolean"
          },
          "name": {
           "description": "FlowStep name",
           "type": "string"
          },
          "policy": {
           "description": "FlowStep policy",
           "type": "string"
          }
         },
         "required": [
          "enabled"
         ],
         "type": "object"
        },
        "type": "array"
       },
       "pre": {
        "description": "Flow pre step",
        "items": {
         "properties": {
          "condition": {
           "description": "FlowStep condition",
           "type": "string"
          },
          "configuration": {
           "description": "FlowStep configuration is a map of arbitrary key-values",
           "format": "textarea",
           "type": "string"
          },
          "description": {
           "description": "FlowStep description",
           "type": "string"
          },
          "enabled": {
           "default": true,
           "description": "Indicate if this FlowStep is enabled or not",
           "type": "boolean"
          },
          "name": {
           "description": "FlowStep name",
           "type": "string"
          },
          "policy": {
           "description": "FlowStep policy",
           "type": "string"
          }
         },
         "required": [
          "enabled"
         ],
         "type": "object"
        },
        "type": "array"
       }
      },
      "required": [
       "enabled"
      ],
      "type": "object"
     },
     "type": "array"
    },
    "gravitee": {
     "default": "2.0.0",
     "description": "The definition version of the API. For v1alpha1 resources, this field should always set to `2.0.0`.",
     "type": "string"
    },
    "id": {
     "description": "The API ID. If empty, this field will take the value of the `metadata.uid` field of the resource.",
     "type": "string"
    },
    "labels": {
     "description": "List of labels of the API",
     "items": {
      "type": "string"
     },
     "type": "array"
    },
    "lifecycle_state": {
     "default": "CREATED",
     "description": "API life cycle state can be one of the values CREATED, PUBLISHED, UNPUBLISHED, DEPRECATED, ARCHIVED",
     "enum": [
      "CREATED",
      "PUBLISHED",
      "UNPUBLISHED",
      "DEPRECATED",
      "ARCHIVED"
     ],
     "type": "string"
    },
    "local": {
     "default": true,
     "description": "local defines if the api is local or not. \n If true, the Operator will create the ConfigMaps for the Gateway and pushes the API to the Management API but without setting the update flag in the datastore. \n If false, the Operator will not create the ConfigMaps for the Gateway. Instead, it pushes the API to the Management API and forces it to update the event in the datastore. This will cause Gateways to fetch the APIs from the datastore",
     "type": "boolean"
    },
    "metadata": {
     "description": "List of API metadata entries",
     "items": {
      "properties": {
       "defaultValue": {
        "description": "MetaData Default value",
        "type": "string"
       },
       "format": {
        "description": "MetaData Format",
        "enum": [
         "STRING",
         "NUMERIC",
         "BOOLEAN",
         "DATE",
         "MAIL",
         "URL"
        ],
        "type": "string"
       },
       "key": {
        "description": "MetaData Key",
        "type": "string"
       },
       "name": {
        "description": "MetaData Name",
        "type": "string"
       },
       "value": {
        "description": "MetaData Value",
        "type": "string"
       }
      },
      "required": [
       "format",
       "key",
       "name"
      ],
      "type": "object"
     },
     "type": "array"
    },
    "name": {
     "description": "API name",
     "type": "string"
    },
    "path_mappings": {
     "description": "API Path mapping",
     "items": {
      "type": "string"
     },
     "type": "array"
    },
    "plans": {
     "description": "API plans",
     "items": {
      "properties": {
       "api": {
        "description": "Specify the API associated with this plan",
        "type": "string"
       },
       "characteristics": {
        "description": "List of plan characteristics",
        "items": {
         "type": "string"
        },
        "type": "array"
       },
       "comment_required": {
        "description": "Indicate of comment is required for this plan or not",
        "type": "boolean"
       },
       "crossId": {
        "description": "The plan Cross ID. This field is used to identify plans defined for an API that has been promoted between different environments.",
        "type": "string"
       },
       "description": {
        "description": "Plan Description",
        "type": "string"
       },
       "excluded_groups": {
        "description": "List of excluded groups for this plan",
        "items": {
         "type": "string"
        },
        "type": "array"
       },
       "flows": {
        "description": "List of different flows for this Plan",
        "items": {
         "properties": {
          "condition": {
           "description": "Flow condition",
           "type": "string"
          },
          "consumers": {
           "description": "List of the consumers of this Flow",
           "items": {
            "properties": {
             "consumerId": {
              "description": "Consumer ID",
              "type": "string"
             },
             "consumerType": {
              "description": "Consumer type (possible values TAG)",
              "type": "integer"
             }
            },
            "type": "object"
           },
           "type": "array"
          },
          "enabled": {
           "default": true,
           "description": "Indicate if this flow is enabled or disabled",
           "type": "boolean"
          },
          "id": {
           "description": "Flow ID",
           "type": "string"
          },
          "methods": {
           "description": "A list of methods  for this flow (GET;POST;PUT;PATCH;DELETE;OPTIONS;HEAD;CONNECT;TRACE;OTHER)",
           "items": {
            "enum": [
             "GET",
             "POST",
             "PUT",
             "PATCH",
             "DELETE",
             "OPTIONS",
             "HEAD",
             "CONNECT",
             "TRACE",
             "OTHER"
            ],
            "type": "string"
           },
           "type": "array"
          },
          "name": {
           "description": "Flow name",
           "type": "string"
          },
          "path-operator": {
           "description": "List of path operators",
           "properties": {
            "operator": {
             "default": "STARTS_WITH",
             "description": "Operator (possible values STARTS_WITH or EQUALS)",
             "type": "string"
            },
            "path": {
             "description": "Operator path",
             "type": "string"
            }
           },
           "type": "object"
          },
          "post": {
           "description": "Flow post step",
           "items": {
            "properties": {
             "condition": {
              "description": "FlowStep condition",
              "type": "string"
             },
             "configuration": {
              "description": "FlowStep configuration is a map of arbitrary key-values",
              "format": "textarea",
              "type": "string"
             },
             "description": {
              "description": "FlowStep description",
              "type": "string"
             },
             "enabled": {
              "default": true,
              "description": "Indicate if this FlowStep is enabled or not",
              "type": "boolean"
             },
             "name": {
              "description": "FlowStep name",
              "type": "string"
             },
             "policy": {
              "description": "FlowStep policy",
              "type": "string"
             }
            },
            "required": [
             "enabled"
            ],
            "type": "object"
           },
           "type": "array"
          },
          "pre": {
           "description": "Flow pre step",
           "items": {
            "properties": {
             "condition": {
              "description": "FlowStep condition",
              "type": "string"
             },
             "configuration": {
              "description": "FlowStep configuration is a map of arbitrary key-values",
              "format": "textarea",
              "type": "string"
             },
             "description": {
              "description": "FlowStep description",
              "type": "string"
             },
             "enabled": {
              "default": true,
              "description": "Indicate if this FlowStep is enabled or not",
              "type": "boolean"
             },
             "name": {
              "description": "FlowStep name",
              "type": "string"
             },
             "policy": {
              "description": "FlowStep policy",
              "type": "string"
             }
            },
            "required": [
             "enabled"
            ],
            "type": "object"
           },
           "type": "array"
          }
         },
         "required": [
          "enabled"
         ],
         "type": "object"
        },
        "type": "array"
       },
       "id": {
        "description": "Plan ID",
        "type": "string"
       },
       "name": {
        "description": "Plan name",
        "type": "string"
       },
       "order": {
        "description": "Plan order",
        "type": "integer"
       },
       "paths": {
        "additionalProperties": {
         "items": {
          "properties": {
           "description": {
            "description": "Rule description",
            "type": "string"
           },
           "enabled": {
            "description": "Indicate if the Rule is enabled or not",
            "type": "boolean"
           },
           "methods": {
            "description": "List of http methods for this Rule (GET;POST;PUT;PATCH;DELETE;OPTIONS;HEAD;CONNECT;TRACE;OTHER)",
            "items": {
             "enum": [
              "GET",
              "POST",
              "PUT",
              "PATCH",
              "DELETE",
              "OPTIONS",
              "HEAD",
              "CONNECT",
              "TRACE",
              "OTHER"
             ],
             "type": "string"
            },
            "type": "array"
           },
           "policy": {
            "description": "Rule policy",
            "properties": {
             "configuration": {
              "description": "Policy configuration is a map of arbitrary key-values",
              "format": "textarea",
              "type": "string"
             },
             "name": {
              "description": "Policy name",
              "type": "string"
             }
            },
            "type": "object"
           }
          },
          "type": "object"
         },
         "type": "array"
        },
        "description": "A map of different paths (alongside their Rules) for this Plan",
        "type": "object"
       },
       "security": {
        "description": "Plan Security",
        "type": "string"
       },
       "securityDefinition": {
        "description": "Plan Security definition",
        "type": "string"
       },
       "selectionRule": {
        "description": "Plan selection rule",
        "type": "string"
       },
       "status": {
        "default": "PUBLISHED",
        "description": "The plan status",
        "enum": [
         "STAGING",
         "PUBLISHED",
         "CLOSED",
         "DEPRECATED"
        ],
        "type": "string"
       },
       "tags": {
        "description": "List of plan tags",
        "items": {
         "type": "string"
        },
        "type": "array"
       },
       "type": {
        "default": "API",
        "description": "Plan type",
        "enum": [
         "API",
         "CATALOG"
        ],
        "type": "string"
       },
       "validation": {
        "default": "MANUAL",
        "description": "Plan validation strategy",
        "enum": [
         "AUTO",
         "MANUAL"
        ],
        "type": "string"
       }
      },
      "required": [
       "description",
       "name",
       "security"
      ],
      "type": "object"
     },
     "type": "array"
    },
    "primaryOwner": {
     "description": "Specify the primary member that owns the API",
     "properties": {
      "displayName": {
       "description": "Member Display Name",
       "type": "string"
      },
      "email": {
       "description": "Member email",
       "type": "string"
      },
      "id": {
       "description": "Member ID",
       "type": "string"
      },
      "type": {
       "description": "Member type",
       "type": "string"
      }
     },
     "required": [
      "displayName",
      "email",
      "id",
      "type"
     ],
     "type": "object"
    },
    "properties": {
     "description": "List of Properties for the API",
     "items": {
      "properties": {
       "encrypted": {
        "description": "Property Encrypted or not?",
        "type": "boolean"
       },
       "key": {
        "description": "Property Key",
        "type": "string"
       },
       "value": {
        "description": "Property Value",
        "type": "string"
       }
      },
      "type": "object"
     },
     "type": "array"
    },
    "proxy": {
     "description": "The proxy of the API that specifies its VirtualHosts and Groups.",
     "properties": {
      "cors": {
       "description": "Proxy Cors",
       "properties": {
        "allowCredentials": {
         "description": "Access Control - Allow credentials or not",
         "type": "boolean"
        },
        "allowHeaders": {
         "description": "Access Control - List of allowed headers",
         "items": {
          "type": "string"
         },
         "type": "array"
        },
        "allowMethods": {
         "description": "Access Control - List of allowed methods",
         "items": {
          "type": "string"
         },
         "type": "array"
        },
        "allowOrigin": {
         "description": "Access Control -  List of Allowed origins",
         "items": {
          "type": "string"
         },
         "type": "array"
        },
        "enabled": {
         "description": "Indicate if the cors enabled or not",
         "type": "boolean"
        },
        "exposeHeaders": {
         "description": "Access Control - List of Exposed Headers",
         "items": {
          "type": "string"
         },
         "type": "array"
        },
        "maxAge": {
         "description": "Access Control -  Max age",
         "type": "integer"
        },
        "runPolicies": {
         "default": false,
         "description": "Run policies or not",
         "type": "boolean"
        }
       },
       "required": [
        "allowCredentials",
        "enabled",
        "maxAge"
       ],
       "type": "object"
      },
      "failover": {
       "description": "Proxy Failover",
       "properties": {
        "cases": {
         "description": "List of Failover cases",
         "items": {
          "type": "string"
         },
         "type": "array"
        },
        "maxAttempts": {
         "description": "Maximum number of attempts",
         "type": "integer"
        },
        "retryTimeout": {
         "description": "Retry timeout",
         "format": "int64",
         "type": "integer"
        }
       },
       "type": "object"
      },
      "groups": {
       "description": "List of endpoint groups of the proxy",
       "items": {
        "properties": {
         "endpoints": {
          "description": "List of Endpoints belonging to this group",
          "items": {
           "properties": {
            "-": {
             "description": "The status of the endpoint (Down, TransitionallyDown, TransitionallyUp, Up)",
             "type": "integer"
            },
            "backup": {
             "default": false,
             "description": "Indicate that this ia a back-end endpoint",
             "type": "boolean"
            },
            "headers": {
             "description": "List of headers needed for this endpoint",
             "items": {
              "properties": {
               "name": {
                "description": "The HTTP header name",
                "type": "string"
               },
               "value": {
                "description": "The HTTP header value",
                "type": "string"
               }
              },
              "type": "object"
             },
             "type": "array"
            },
            "healthcheck": {
             "description": "Specify EndpointHealthCheck service settings",
             "properties": {
              "enabled": {
               "default": false,
               "description": "Service is enabled or not?",
               "type": "boolean"
              },
              "inherit": {
               "description": "Is service inherited or not?",
               "type": "boolean"
              },
              "name": {
               "description": "Service name",
               "type": "string"
              },
              "schedule": {
               "description": "Schedule",
               "type": "string"
              },
              "steps": {
               "description": "List of health check steps",
               "items": {
                "properties": {
                 "name": {
                  "description": "Health Check Step Name",
                  "type": "string"
                 },
                 "request": {
                  "description": "Health Check Step Request",
                  "properties": {
                   "body": {
                    "description": "Health Check Request Body",
                    "type": "string"
                   },
                   "fromRoot": {
                    "description": "If true, the health check request will be issued without prepending the context path of the API.",
                    "type": "boolean"
                   },
                   "headers": {
                    "description": "List of HTTP headers to include in the health check request",
                    "items": {
                     "properties": {
                      "name": {
                       "description": "The HTTP header name",
                       "type": "string"
                      },
                      "value": {
                       "description": "The HTTP header value",
                       "type": "string"
                      }
                     },
                     "type": "object"
                    },
                    "type": "array"
                   },
                   "method": {
                    "description": "The HTTP method to use when issuing the health check request",
                    "enum": [
                     "GET",
                     "POST",
                     "PUT",
                     "PATCH",
                     "DELETE",
                     "OPTIONS",
                     "HEAD",
                     "CONNECT",
                     "TRACE",
                     "OTHER"
                    ],
                    "type": "string"
                   },
                   "path": {
                    "description": "The path of the endpoint handling the health check request",
                    "type": "string"
                   }
                  },
                  "required": [
                   "fromRoot"
                  ],
                  "type": "object"
                 },
                 "response": {
                  "description": "Health Check Step Response",
                  "properties": {
                   "assertions": {
                    "items": {
                     "type": "string"
                    },
                    "type": "array"
                   }
                  },
                  "type": "object"
                 }
                },
                "type": "object"
               },
               "type": "array"
              }
             },
             "required": [
              "enabled",
              "inherit"
             ],
             "type": "object"
            },
            "http": {
             "description": "Custom HTTP client options used for this endpoint",
             "properties": {
              "clearTextUpgrade": {
               "description": "Should HTTP/2 clear text upgrade be used or not ?",
               "type": "boolean"
              },
              "connectTimeout": {
               "description": "Connection timeout of the http connection",
               "format": "int64",
               "type": "integer"
              },
              "followRedirects": {
               "description": "Should HTTP redirects be followed or not ?",
               "type": "boolean"
              },
              "idleTimeout": {
               "description": "Idle Timeout for the http connection",
               "format": "int64",
               "type": "integer"
              },
              "keepAlive": {
               "description": "Should keep alive be used for the HTTP connection ?",
               "type": "boolean"
              },
              "maxConcurrentConnections": {
               "description": "HTTP max concurrent connections",
               "type": "integer"
              },
              "pipelining": {
               "description": "Should HTTP/1.1 pipelining be used for the connection or not ?",
               "type": "boolean"
              },
              "readTimeout": {
               "description": "Read timeout",
               "format": "int64",
               "type": "integer"
              },
              "useCompression": {
               "description": "Should compression be used or not ?",
               "type": "boolean"
              },
              "version": {
               "description": "HTTP Protocol Version (Possible values Http1 or Http2)",
               "type": "string"
              }
             },
             "type": "object"
            },
            "inherit": {
             "description": "Is endpoint inherited or not",
             "type": "boolean"
            },
            "name": {
             "description": "Name of the endpoint",
             "type": "string"
            },
            "proxy": {
             "description": "Configure the HTTP Proxy settings to reach target if needed",
             "properties": {
              "enabled": {
               "description": "Specifies that the HTTP connection will be established through a proxy",
               "type": "boolean"
              },
              "host": {
               "description": "Proxy host name",
               "type": "string"
              },
              "password": {
               "description": "The HTTP proxy password (if the proxy requires authentication)",
               "type": "string"
              },
              "port": {
               "description": "The HTTP proxy port",
               "type": "integer"
              },
              "type": {
               "description": "The HTTP proxy type (possible values Http, Socks4, Socks5)",
               "type": "string"
              },
              "useSystemProxy": {
               "description": "If true, the proxy defined at the system level will be used",
               "type": "boolean"
              },
              "username": {
               "description": "The HTTP proxy username (if the proxy requires authentication)",
               "type": "string"
              }
