schema:
{
 "description": "Applications resource allows to use the operator to create and manage applications in Gravitee API Management. This means that a management context must be define in the specification when creating an application.",
 "properties": {
  "spec": {
   "properties": {
    "app_key_mode": {
     "description": "The API key mode to use. If shared, the application will reuse the same API key across various subscriptions.",
     "enum": [
      "SHARED",
      "EXCLUSIVE",
      "UNSPECIFIED"
     ],
     "type": "string"
    },
    "applicationMetaData": {
     "description": "Application meta data",
     "items": {
      "properties": {
       "applicationId": {
        "description": "Meta data ApplicationId",
        "type": "string"
       },
       "defaultValue": {
        "description": "Meta data DefaultValue",
        "type": "string"
       },
       "format": {
        "description": "Meta data Format",
        "enum": [
         "STRING",
         "NUMERIC",
         "BOOLEAN",
         "DATE",
         "MAIL",
         "URL"
        ],
        "type": "string"
       },
       "hidden": {
        "description": "Meta data is hidden or not?",
        "type": "boolean"
       },
       "key": {
        "description": "Meta data Key",
        "type": "string"
       },
       "name": {
        "description": "Meta data Name",
        "type": "string"
       },
       "value": {
        "description": "Meta data Value",
        "type": "string"
       }
      },
      "required": [
       "name"
      ],
      "type": "object"
     },
     "type": "array"
    },
    "background": {
     "description": "The base64 encoded background to use for this application when displaying it on the portal",
     "type": "string"
    },
    "clientId": {
     "description": "The ClientId identifying the application. This field is required when subscribing to an OAUTH2 / JWT plan.",
     "type": "string"
    },
    "contextRef": {
     "description": "The contextRef field allows to reference a previously created ManagementContext. See \u003ca href=\"#managementcontext\"\u003eManagementContext\u003c/a\u003e for more information.",
     "properties": {
      "name": {
       "type": "string"
      },
      "namespace": {
       "type": "string"
      }
     },
     "required": [
      "name"
     ],
     "type": "object"
    },
    "description": {
     "description": "Application Description",
     "type": "string"
    },
    "disable_membership_notifications": {
     "description": "Should membership notifications be disabled or not ?",
     "type": "boolean"
    },
    "domain": {
     "description": "Application domain",
     "type": "string"
    },
    "groups": {
     "description": "Application groups",
     "items": {
      "type": "string"
     },
     "type": "array"
    },
    "id": {
     "description": "Application ID",
     "type": "string"
    },
    "metadata": {
     "description": "Application Metadata, a map of arbitrary key-values",
     "format": "textarea",
     "type": "string"
    },
    "name": {
     "description": "Application name",
     "type": "string"
    },
    "origin": {
     "description": "The origin which is used to create this Application",
     "enum": [
      "kubernetes"
     ],
     "type": "string"
    },
    "picture": {
     "description": "The base64 encoded picture to use for this application when displaying it on the portal (if not relying on an URL)",
     "type": "string"
    },
    "picture_url": {
     "description": "An URL pointing to the picture to use when displaying the application on the portal",
     "type": "string"
    },
    "redirectUris": {
     "description": "List of application Redirect Uris",
     "items": {
      "type": "string"
     },
     "type": "array"
    },
    "settings": {
     "description": "Application settings",
     "properties": {
      "app": {
       "properties": {
        "client_id": {
         "description": "ClientId is the client id of the application",
         "type": "string"
        },
        "type": {
         "description": "Application Type",
         "type": "string"
        }
       },
       "type": "object"
      },
      "oauth": {
       "properties": {
        "application_type": {
         "description": "Oauth client application type",
         "type": "string"
        },
        "client_id": {
         "description": "Oauth client id",
         "type": "string"
        },
        "client_secret": {
         "description": "Oauth client secret",
         "type": "string"
        },
        "client_uri": {
         "description": "Oauth client uri",
         "type": "string"
        },
        "grant_types": {
         "description": "List of Oauth client grant types",
         "items": {
          "type": "string"
         },
         "type": "array"
        },
        "logo_uri": {
         "description": "Oauth client logo uri",
         "type": "string"
        },
        "redirect_uris": {
         "description": "List of Oauth client redirect uris",
         "items": {
          "type": "string"
         },
         "type": "array"
        },
        "renew_client_secret_supported": {
         "description": "Whether client secret renewing is supported or not",
         "type": "boolean"
        },
        "response_types": {
         "description": "List of Oauth client response types",
         "items": {
          "type": "string"
         },
         "type": "array"
        }
       },
       "type": "object"
      }
     },
     "type": "object"
    },
    "type": {
     "description": "Application Type",
     "type": "string"
    }
   },
   "required": [
    "name"
   ],
   "type": "object"
  }
 },
 "title": "Application",
 "type": "object"
}
