schema:
{
 "description": "The Management Context is used to sync resources with an APIM environment. The APIM instance is identified by a URL, an organization ID and an environment ID. Authentication can be defined under the form of an access token or basic authentication credentials, or using a reference to a Kubernetes secret holding one of those authentication methods.",
 "properties": {
  "spec": {
   "description": "ManagementContext represents the configuration for a specific environment",
   "properties": {
    "auth": {
     "description": "Auth defines the authentication method used to connect to the API Management. Can be either basic authentication credentials, a bearer token or a reference to a kubernetes secret holding one of these two configurations.",
     "properties": {
      "bearerToken": {
       "description": "The bearer token used to authenticate against the API Management instance (must be generated from an admin account)",
       "type": "string"
      },
      "credentials": {
       "description": "The Basic credentials used to authenticate against the API Management instance.",
       "properties": {
        "password": {
         "type": "string"
        },
        "username": {
         "type": "string"
        }
       },
       "type": "object"
      },
      "secretRef": {
       "description": "A secret reference holding either a bearer token or the user name and password used for basic authentication. Omitting the namespace field will default to the namespace of the resource.",
       "properties": {
        "name": {
         "type": "string"
        },
        "namespace": {
         "type": "string"
        }
       },
       "required": [
        "name"
       ],
       "type": "object"
      }
     },
     "type": "object"
    },
    "baseUrl": {
     "description": "The baseURL of a management API instance",
     "pattern": "^http(s?):\\/\\/.+$",
     "type": "string"
    },
    "environmentId": {
     "description": "An existing environment id targeted by the context within the organization.",
     "type": "string"
    },
    "organizationId": {
     "description": "The Gravitee APIM organization targeted by the management context.",
     "type": "string"
    }
   },
   "required": [
    "auth",
    "baseUrl",
    "environmentId",
    "organizationId"
   ],
   "type": "object"
  }
 },
 "title": "Management Context",
 "type": "object"
}
