schema:
{
 "properties": {
  "spec": {
   "properties": {
    "nodeGroups": {
     "items": {
      "properties": {
       "allocatable": {
        "additionalProperties": {
         "anyOf": [
          {
           "type": "integer"
          },
          {
           "type": "string"
          }
         ],
         "pattern": "^(\\+|-)?(([0-9]+(\\.[0-9]*)?)|(\\.[0-9]+))(([KMGTPE]i)|[numkMGTPE]|([eE](\\+|-)?(([0-9]+(\\.[0-9]*)?)|(\\.[0-9]+))))?$",
