schema:
{
 "description": "Database is the Schema for the databases API",
 "properties": {
  "spec": {
   "description": "DatabaseSpec defines the desired state of Database",
   "properties": {
    "databaseInstance": {
     "description": "Database instance specific details",
     "properties": {
      "credentialSecret": {
       "description": "Name of the secret holding the credentials for the database instance (password and ssh key)",
       "type": "string"
      },
      "databaseInstanceName": {
       "description": "Name of the database instance",
       "type": "string"
      },
      "databaseNames": {
       "description": "Name(s) of the database(s) to be provisiond inside the database instance default [ \"database_one\", \"database_two\", \"database_three\" ]",
       "items": {
        "type": "string"
       },
       "type": "array"
      },
      "description": {
       "description": "Description of the database instance",
       "type": "string"
      },
      "profiles": {
       "properties": {
        "compute": {
         "properties": {
          "id": {
           "type": "string"
          },
          "name": {
           "type": "string"
          }
         },
         "type": "object"
        },
        "dbParam": {
         "properties": {
          "id": {
           "type": "string"
          },
          "name": {
           "type": "string"
          }
         },
         "type": "object"
        },
        "dbParamInstance": {
         "properties": {
          "id": {
           "type": "string"
          },
          "name": {
           "type": "string"
          }
         },
         "type": "object"
        },
        "network": {
         "properties": {
          "id": {
           "type": "string"
          },
          "name": {
           "type": "string"
          }
         },
         "type": "object"
        },
        "software": {
         "properties": {
          "id": {
           "type": "string"
          },
          "name": {
           "type": "string"
          }
         },
         "type": "object"
        }
       },
       "type": "object"
      },
      "size": {
       "description": "Size of the database instance, minimum 10 (GBs)",
       "type": "integer"
      },
      "timeMachine": {
       "description": "Information related to time machine that is to be associated with this database",
       "properties": {
        "dailySnapshotTime": {
         "description": "Daily snapshot time in HH:MM:SS (24 hour format)",
         "type": "string"
        },
        "description": {
         "type": "string"
        },
        "logCatchUpFrequency": {
         "description": "Log catch up frequency in minutes",
         "type": "integer"
        },
        "monthlySnapshotDay": {
         "description": "Day of the month for monthly snapshot",
         "type": "integer"
        },
        "name": {
         "type": "string"
        },
        "quarterlySnapshotMonth": {
         "description": "Start month for the quarterly snapshot Jan =\u003e Jan, Apr, Jul, Oct. Feb =\u003e Feb, May, Aug, Nov. Mar =\u003e Mar, Jun, Sep, Dec.",
         "type": "string"
        },
        "sla": {
         "description": "Name of the SLA to be used, default NONE",
         "type": "string"
        },
        "snapshotsPerDay": {
         "description": "Number of snapshots per day",
         "type": "integer"
        },
        "weeklySnapshotDay": {
         "description": "Day of the week for weekly snapshot",
         "type": "string"
        }
       },
       "type": "object"
      },
      "timezone": {
       "description": "default UTC",
       "type": "string"
      },
      "type": {
       "type": "string"
      }
     },
     "required": [
      "credentialSecret",
      "databaseInstanceName",
      "size",
      "type"
     ],
     "type": "object"
    },
    "ndb": {
     "description": "Details of the NDB installation",
     "properties": {
      "clusterId": {
       "type": "string"
      },
      "credentialSecret": {
       "description": "Name of the secret holding the credentials for NDB (username and password)",
       "type": "string"
      },
      "server": {
       "description": "NDB Server URL",
       "type": "string"
      },
      "skipCertificateVerification": {
       "description": "Skip server's certificate and hostname verification",
       "type": "boolean"
      }
     },
     "required": [
      "clusterId",
      "credentialSecret",
      "server",
      "skipCertificateVerification"
     ],
     "type": "object"
    }
   },
   "required": [
    "databaseInstance",
    "ndb"
   ],
   "type": "object"
  }
 },
 "title": "Database",
 "type": "object"
}
